gTeamSpeak.phpcode:
if ($_REQUEST['do'] == 'sadmin_messageUser')
{

	print_form_header('', '', 0, 1, 'gtspruneusers');
	print_table_header('Message to User');

	if ( empty($_REQUEST['message']) ) {
		print_description_row('Please enter Message.','','','','center');
	} else {

		$array = explode("\n", $_REQUEST["message"]);
		foreach ($array as $line) {
		   $cyts->sadmin_messageUser($_REQUEST["userid"], $line, 'true');
		}

		print_description_row(nl2br(htmlspecialchars($_REQUEST["message"])));
	}

	print_table_footer(2, construct_button_code($vbphrase['go_back'], 'javascript:history.back(1)'));
}

gTeamSpeak.functions:
function func_gts_sa_messageuser()
{
	global $vbulletin;
	global $cyts;

	$pl = $cyts->info_playerList();

	$plarray = array();

	foreach ($pl AS $user)
	{
		$chan = $cyts->info_channelInfo($user['c_id']);

		$plarray[$user['p_id']] = $user['nick'];
	}

	asort($plarray);

	return $plarray;
}
