		$char['faction'] = ucfirst(strtolower(trim($char['faction'])));
		$char['character'] = ucfirst(strtolower(trim(strip_tags($char['character']))));
		$char['realmurl'] = urlencode($char['realm']);
		$char['characterurl'] = rawurlencode($char['character']);

