code.uninstallcode:
$db->query_write("ALTER TABLE ".TABLE_PREFIX."event DROP gwe_signup");

if($vbulletin->options['gwe_uninstall_history'])
{
	$vbulletin->db->query("DROP TABLE IF EXISTS " . TABLE_PREFIX . "gwowevents;");
}

plugin.phpcode:
require_once('./includes/functions_gwowevents.php');
$vbphrase['gllc_x_copy_x'] = construct_phrase('%s, Copyright &copy; 2006 - %s, <a href="https://github.com/ghryphen" target="_blank">Ghryphen</a>', 'gWoWEvents', $copyrightyear);

// Signup POST
if ($_REQUEST['gwe_do'] == 'signup' AND $_REQUEST['do'] == 'getinfo')
{
	$vbulletin->input->clean_array_gpc('p', array(
		'response' => TYPE_INT,
		'realmchar' => TYPE_STR,
		'locale' => TYPE_STR,
		'comment' => TYPE_NOHTML,
		'prevsign' => TYPE_INT
	));

	list($submit['realm'], $submit['character']) = explode(".", $vbulletin->GPC['realmchar']);

	if (strlen($vbulletin->GPC['comment']) > $vbulletin->options['gwe_commentmax'])
	{
		$submit['comment'] = substr($vbulletin->GPC['comment'], 0, $vbulletin->options['gwe_commentmax'] - 3) . '...';
	}
	else
	{
		$submit['comment'] = $vbulletin->GPC['comment'];
	}

	if ($vbulletin->GPC['realmchar'] != '0' && in_array($vbulletin->GPC['response'], array('1', '2', '3', '4')))
	{
		$makepost = true;
		$errormessage = '';

		if ($vbulletin->options['gwe_commentenabled'] && ($vbulletin->GPC['response'] == '2' || $vbulletin->GPC['response'] == '3'))
		{
			if(strlen($submit['comment']) < $vbulletin->options['gwe_commentmin'])
			{
				$makepost = false;
				$errormessage = construct_phrase($vbphrase['gwe_comment_required'], $vbulletin->options['gwe_commentmin']);
			}
			else
			{
				$makepost = true;
			}
		}
	}

	if ($makepost)
	{
		$res = $db->query_read("SELECT * FROM	".TABLE_PREFIX."gwowevents WHERE `userid` = '" . $vbulletin->userinfo['userid'] . "' AND `armorydata` = '1' ORDER BY date DESC LIMIT 1");

		while ($prev = $vbulletin->db->fetch_array($res))
		{
			$previous_data = "
				`charurl` = '" . $prev['charurl'] . "' ,
				`faction` = '" . $prev['faction'] . "' ,
				`factionid` = '" . $prev['factionid'] . "' ,
				`gender` = '" . $prev['gender'] . "' ,
				`genderid` = '" . $prev['genderid'] . "' ,
				`race` = '" . $prev['race'] . "' ,
				`raceid` = '" . $prev['raceid'] . "' ,
				`class` = '" . $prev['class'] . "' ,
				`classid` = '" . $prev['classid'] . "' ,
			";
		}

		$write_query = "
			`eventid` = '" . $eventinfo['eventid'] . "' ,
			`userid` = '" . $vbulletin->userinfo['userid'] . "' ,
			`response` = '" . $vbulletin->GPC['response'] . "' ,
			`comment` = '" . $db->escape_string($submit['comment']) . "' ,
			`armorydata` = '" . 0 . "' ,
			`locale` = '" . $db->escape_string($vbulletin->GPC['locale']) . "' ,
			`realmchar` = '" . $db->escape_string($vbulletin->GPC['realmchar']) . "' ,
			`realm` = '" . $db->escape_string($submit['realm']) . "' ,
			`character` = '" . $db->escape_string($submit['character']) . "' ,
			" . $previous_data . "
		";

		if ($vbulletin->GPC['prevsign'])
		{
			$write_query .= "`date` = '" . $vbulletin->GPC['prevsign'] . "' ,";
		}
		else
		{
			$write_query .= "`date` = '" . TIMENOW . "' ,";
		}

		$vbulletin->db->query_write("REPLACE INTO " . TABLE_PREFIX . "gwowevents SET " . $write_query . " changed = '" . TIMENOW . "'");
	}
}
else
{
	// Log visitors
	if ($vbulletin->userinfo['userid'] && $eventinfo['eventid'])
	{
		$vbulletin->db->query_write("
			REPLACE INTO " . TABLE_PREFIX . "gwowevents_visitor
				(eventid, visitorid, dateline, visible)
			VALUES
				(
					" . $eventinfo['eventid'] . ",
					" . $vbulletin->userinfo['userid'] . ",
					" . TIMENOW . ",
					" . ($vbulletin->userinfo['options'] & $vbulletin->bf_misc_useroptions['invisible'] ? 0 : 1) . "
				)
		");
	}
}

// Permission
if (is_member_of($vbulletin->userinfo, explode(',', $vbulletin->options['gwe_usergroup'])))
{
	$show['signupauth'] = 1;
}

// Allow Pre signup
if((TIMENOW + $offset + ($vbulletin->options['gwe_pre_signup'] * 86400)) >= $eventinfo['dateline_to_user'])
{
	$eventinfo['gwe_pre_signup'] = true;
}
else
{
	$eventinfo['gwe_pre_signup'] = false;
}

// Allow Post signup
if(TIMENOW <= $eventinfo['dateline_from_user'])
{
	$eventinfo['gwe_post_signup'] = true;
}
else
{
	$eventinfo['gwe_post_signup'] = false;
}

// Vistor output
if($show['signupauth'] && $eventinfo['eventid'] && $vbulletin->options['gwe_show_visitors'])
{
	$visitors_db = $vbulletin->db->query_read_slave("
		SELECT user.userid, user.username, user.usergroupid, user.displaygroupid, gwowevents_visitor.visible, gwowevents_visitor.dateline
		FROM " . TABLE_PREFIX . "gwowevents_visitor AS gwowevents_visitor
		INNER JOIN " . TABLE_PREFIX . "user AS user ON (user.userid = gwowevents_visitor.visitorid)
		WHERE gwowevents_visitor.eventid = " . $eventinfo['eventid'] . "
			" . (!($vbulletin->userinfo['permissions']['genericpermissions'] & $vbulletin->bf_ugp_genericpermissions['canseehidden']) ? " AND (visible = 1 OR gwowevents_visitor.visitorid = " . $vbulletin->userinfo['userid'] . ")" : "") . "
		ORDER BY user.username ASC
	");

	$visitors = array();
	while ($user = $vbulletin->db->fetch_array($visitors_db))
	{
		$visitors["$user[username]"] = $user;
	}

	uksort($visitors, 'strnatcasecmp');

	if (!is_array($buddylist))
	{
		if ($vbulletin->userinfo['buddylist'] = trim($vbulletin->userinfo['buddylist']))
		{
			$buddylist = preg_split('/\s+/', $vbulletin->userinfo['buddylist'], -1, PREG_SPLIT_NO_EMPTY);
		}
		else
		{
			$buddylist = array();
		}
	}

	$visitorbits = '';
	foreach ($visitors AS $user)
	{
		fetch_musername($user);
		$user['invisiblemark'] = !$user['visible'] ? '*' : '';
		$user['buddymark'] = in_array($user['userid'], $buddylist) ? '+' : '';
		$user['view_date'] = vbdate($vbulletin->options['dateformat'], $user['dateline'], 1);
		$user['view_time'] = vbdate($vbulletin->options['timeformat'], $user['dateline']);
		eval('$visitorbits .= "' . fetch_template('gwe_visitorbit') . '";');
	}
	$visitorbits = substr($visitorbits, 0, -2);
}

// fieldID or ID
if (is_numeric($vbulletin->options['gwe_fieldid']))
{
	$gwe_fieldid = "field" . $vbulletin->options['gwe_fieldid'];
}
else
{
	$gwe_fieldid = $vbulletin->options['gwe_fieldid'];
}

// Sort
switch($vbulletin->options['gwe_signup_sort'])
{
	case 0: $orderby = "gwe.date"; break;
	case 1: $orderby = "gwe.character"; break;
	case 2: $orderby = "gwe.class"; break;
};

// Signups
$res = $db->query_read("
	SELECT	gwe.*,
		user.username,
		userfield.$gwe_fieldid
	FROM
		".TABLE_PREFIX."gwowevents gwe
	LEFT JOIN
		".TABLE_PREFIX."userfield AS userfield ON userfield.userid = gwe.userid
	LEFT JOIN
		".TABLE_PREFIX."user AS user ON user.userid = gwe.userid
	WHERE
		eventid = 0".$eventinfo['eventid']."
	ORDER BY
		gwe.response ASC, ".$orderby."
");

while ($attendee = $db->fetch_array($res))
{
	// Spec
	if (is_numeric($attendee["talent1"]))
	{
		$spec = array($attendee["talent1"], $attendee["talent2"], $attendee["talent3"]);

		arsort($spec);

		$spec = array_keys($spec);

		if ($attendee["talent" . ($spec[0] + 1)] == 0)
		{
			$attendee["spec1"] = "None";
			$attendee["spec2"] = "";
		} else {
			$lim = $attendee["talent" . ($spec[0] + 1)] / 2;

			if ($attendee["talent" . ($spec[2] + 1)] >= $lim)
			{
				$attendee["spec1"] = "Hybrid";
				$attendee["spec2"] = "";
			} else if ($attendee["talent" . ($spec[1] + 1)] >= $lim) {
				$attendee["spec1"] = $spec[0] + 1;
				$attendee["spec1phrase"] = $gwephrase['spec'][$attendee['classid']][$attendee['spec1']];
				$attendee["spec2"] = $spec[1] + 1;
				$attendee["spec2phrase"] = $gwephrase['spec'][$attendee['classid']][$attendee['spec2']];;
			} else {
				$attendee["spec1"] = $spec[0] + 1;
				$attendee["spec1phrase"] = $gwephrase['spec'][$attendee['classid']][$attendee['spec1']];
				$attendee["spec2"] = "";
				$attendee["spec2phrase"] = "";
			}
		}

	}
	else
	{
		$attendee["spec1"] = "";
		$attendee["spec2"] = "";
	}

	$attendee['signup_date'] = vbdate($vbulletin->options['dateformat'], $attendee['date'], 1);
	$attendee['signup_time'] = vbdate($vbulletin->options['timeformat'], $attendee['date']);

	if ($attendee['response'] == '1')
	{
		$attendeecount['yes']++;
		$attendeeclasscount[$attendee['classid']]++;
		eval('$attendee_yes .= "' . fetch_template('gwe_signup_bit') . '";');
	}
	if ($attendee['response'] == '2')
	{
		$attendeecount['late']++;
		eval('$attendee_late .= "' . fetch_template('gwe_signup_bit') . '";');
	}
	if ($attendee['response'] == '3')
	{
		$attendeecount['maybe']++;
		eval('$attendee_maybe .= "' . fetch_template('gwe_signup_bit') . '";');
	}
	if ($attendee['response'] == '4')
	{
		$attendeecount['no']++;
		eval('$attendee_no .= "' . fetch_template('gwe_signup_bit') . '";');
	}

	// Setup users registered info
	if ($attendee['userid'] == $vbulletin->userinfo['userid'])
	{
		$myinfo = $attendee;
	}
}

// Character select menu
$chars = explode("\n", $vbulletin->userinfo[$gwe_fieldid]);

$charcount = 0;

foreach ($chars AS $item)
{
	list($char['locale'], $char['realm'], $char['faction'], $char['character']) = explode("|", $item);

	$char['character'] = trim($char['character']);
	$char['realm'] = trim($char['realm']);
	$char['realmchar'] = $char['realm'] . '.' . $char['character'];

	if (!$vbulletin->options['gwe_allowserver'] || in_array($char['realm'], explode(',', $vbulletin->options['gwe_allowserver'])))
	{
		if ($char['locale'] == $vbulletin->options['gwe_locale'])
		{
			if($myinfo['realmchar'] == $char['realmchar'])
			{
				$char_selected = 'selected';
			}
			else
			{
				$char_selected = '';
			}

			$character_select .= '<option value="' . $char['realmchar'] . '" ' . $char_selected . '>' . $char['realm'] . ' - ' . $char['character'] . '</option>'."\n";
			$charcount++;
		}
	}

	// Stops after first result if alts are not enabled
	if ($charcount == 1 && !$vbulletin->options['gwe_allowalts'])
	{
		break;
	}
}

$timenow = TIMENOW;

eval('$eventinfo[event] .= "' . fetch_template('gwe_signup') . '";');

eval('$gwowevents = "' . fetch_template('gwe_signup_form') . '";');
