if ($vbulletin->options['gxbl_memberinfo_tab'] && function_exists('gxbl_has_access') && gxbl_has_access() && $vbulletin->options['gxbl_xaged_api'])
{
	$blocklist = array_merge($blocklist, array(
		'gxboxlive' => array(
			'class' => 'gxboxlive',
			'title' => $vbphrase['gxboxlive_vbnav'],
			'hook_location' => $vbulletin->options['gxbl_memberinfo_tab'] == 1 ? 'profile_left_first' : 'profile_left_last',
		)
