	class vB_ProfileBlock_gxboxlive extends vB_ProfileBlock
	{
		var $template_name = 'gxbl_memberinfo_block';
		function confirm_empty_wrap()
		{
			return false;
		}

		function confirm_display()
		{
			return ($this->block_data['gxboxlive'] != '');
		}

		function prepare_output($id = '', $options = array())
		{
			global $vbulletin;

			$gamertag = trim(strtolower($this->profile->userinfo["field" . $vbulletin->options['gxbl_user_field']]));

			if ($gamertag)
			{
				require_once(DIR . '/includes/class_gxboxstatus.php');

				$x = new XboxData;
				$x->SetGUID( $vbulletin->options['gxbl_xaged_api'] );
				$x->SetGamertag( $gamertag );

				$profile = $x->GetGamercard();	

				$gamerinfo['avatar'] = $profile['GamerInfo']['tileUrl'];
				$gamerinfo['gamertag'] = $profile['GamerInfo']['gamertag'];
				$gamerinfo['reputationimage'] = $profile['GamerInfo']['reputationUrl'];
				$gamerinfo['gamerscore'] = $profile['GamerInfo']['score'];
				$gamerinfo['zone'] = $profile['GamerInfo']['zone'];
				$gamerinfo['presencestatus'] = $profile['GamerPresence']['StatusText'];
				$gamerinfo['presenceinfo'] = $profile['GamerPresence']['info'];

				if (isset($profile['GamerInfo']['RecentGames']['Game']['tile32Url']))
				{
					$gameinfo['image'] = $profile['GamerInfo']['RecentGames']['Game']['tile64Url'];
					$gameinfo['name'] = $profile['GamerInfo']['RecentGames']['Game']['name'];
					$gameinfo['achievements'] = $profile['GamerInfo']['RecentGames']['Game']['achievements'];
					$gameinfo['totalachievements'] = $profile['GamerInfo']['RecentGames']['Game']['totalAchievements'];
					$gameinfo['gamerscore'] = $profile['GamerInfo']['RecentGames']['Game']['gamerscore'];
					$gameinfo['totalgamerscore'] = $profile['GamerInfo']['RecentGames']['Game']['totalGamerscore'];

					eval('$gxbl_gamebits .= "' . fetch_template('gxbl_memberinfo_block_gamebit') . '";');
				}
				elseif (is_array($profile['GamerInfo']['RecentGames']['Game']))
				{
					foreach ($profile['GamerInfo']['RecentGames']['Game'] as $games)
					{
						$gameinfo['image'] = $games['tile64Url'];
						$gameinfo['name'] = $games['name'];
						$gameinfo['achievements'] = $games['achievements'];
						$gameinfo['totalachievements'] = $games['totalAchievements'];
						$gameinfo['gamerscore'] = $games['gamerscore'];
						$gameinfo['totalgamerscore'] = $games['totalGamerscore'];

						eval('$gxbl_gamebits .= "' . fetch_template('gxbl_memberinfo_block_gamebit') . '";');
					}
				}


				global $copyrightyear;
				$vbphrase['gllc_x_copy_x'] = construct_phrase('%s, Copyright &copy; 2006 - %s, <a href="https://github.com/ghryphen" target="_blank">Ghryphen</a>', 'gXboxLive', $copyrightyear);

				eval('$this->block_data[gxboxlive] = "' . fetch_template('gxbl_memberinfo_block_info') . '";');
			}
		}
	}
}

